	let shortcuts: {[key:string]:string} = {}
	let formatted_shortcuts: {[key:string]:string} = {}

	export function initCustomIcons() {
		for (let [name, [w, h, path]] of Object.entries(uix_icons)) {
			let faSplat = {
				prefix: 'uix',
				iconName: name,
				icon: [
					w, h, [], null, path
				]
			}
			// @ts-ignore
			FontAwesome.library.add(faSplat)
		}
	}

	export async function initShortcuts() {
		for (let [name, shortcut] of Object.entries<string>(default_shortcuts[PLATFORM])) shortcuts[name] = shortcut;
		formatShortcuts();
	}

	export function formatShortcuts() {
		for (let name of Object.keys(shortcuts)) {
			formatShortcut(name)
		}
	}

	export function formatShortcut(name: string) {
		formatted_shortcuts[name] = shortcuts[name]
			?.toLowerCase()
			.replace("delete", "⌦")
			.replace("backspace", "⌫")
			.replace(" ", "␣")
			.replace("cmd", "⌘")
			.replace("ctrl", "⌃")
			.replace("enter", "⏎")
			.replace(/\+/g, "")
			.toUpperCase();
	}

	export function getString(name:string):string {
		return STRINGS[Datex.Runtime.ENV.LANG]?.[name] ?? "?";
	}

	export function getStringReference(name:Datex.CompatValue<string>):Datex.Value<string> {
		return Datex.Runtime.getLocalStringFromMap(STRINGS, name);
	}


	// add new strings in different languages ({en:{a:'a',...},...})
	export function addStrings(strings:Datex.CompatObject<Datex.CompatObject<string>>) {

		for (const [lang,s] of DatexObject.entries(strings)) {
			if (typeof lang != "string") continue;
			if (!(lang in STRINGS)) STRINGS[lang] = {}
			for (const [key, value] of DatexObject.entries(s)) {
				if (typeof key != "string") continue;
				STRINGS[lang][key] = value;
			}
		}

		// update unloaded string values
		Datex.Runtime.newLocalStringsMapValues(STRINGS)
	}


	/** public methods to add additional custom shortcuts */
	// 'shortcut_name', 'shortcut' | {windows:'x', macos:'y'}
	export function addShortcut(name:string, shortcut:string|object) {
		if (typeof shortcut == "string") shortcuts[name] = shortcut;
		else shortcuts[name] = shortcut[PLATFORM];
		formatShortcut(name);
	}

	// {shortcut_name: 'shortcut' | {windows:'x', macos:'y'}}
	export function addShortcuts(shortcuts: object) {
		for (let [name, shortcut] of Object.entries(shortcuts)) {
			addShortcut(name, shortcut)
		}
	}

	export function getShortcut(name:string):string {
		return shortcuts[name]?.toLowerCase() ?? "?";
	}

	export function getShortcutFormatted(name:string):string {
		return formatted_shortcuts[name] ?? "?";
	}

	export function loadCSS(src:string) {
		if (IS_HEADLESS) return;
		return new Promise( resolve=> {
			let link = document.createElement( 'link' );
			link.rel  = 'stylesheet';
			link.href = src;
			//link.crossOrigin = 'use-credentials';
			document.head.appendChild( link );
			link.onload = resolve;
			link.onerror = function () {
				logger.error("could not load css file " + src)
			};
		});
	}

	export function loadJSLib(src:string) {
		if (IS_HEADLESS) return;
		return new Promise(resolve =>{
			let script = document.createElement('script');
			script.onload = resolve;
			script.onerror = function () {
				logger.error("could not load js library " + src)
			};
			script.src = src;
			document.head.appendChild(script);
		})
	}
